const e=JSON.parse('{"key":"v-68edbb26","path":"/os/windows/Windows10%E6%9C%AC%E5%9C%B0%E7%A1%AC%E7%9B%98%E5%8F%98%E6%88%90%E4%BA%86%E5%8F%AF%E5%BC%B9%E5%87%BA%E7%A3%81%E7%9B%98.html","title":"Windows10本地硬盘变成了可弹出磁盘","lang":"zh-CN","frontmatter":{"icon":"disk","author":"Regnaiq","title":"Windows10本地硬盘变成了可弹出磁盘","date":"2022-05-14T00:00:00.000Z","category":["操作系统","Windows"],"tag":["Windows","AHCI","硬盘"]},"headers":[{"level":2,"title":"问题","slug":"问题","link":"#问题","children":[]},{"level":2,"title":"原因","slug":"原因","link":"#原因","children":[]},{"level":2,"title":"解决方法","slug":"解决方法","link":"#解决方法","children":[]}],"git":{"createdTime":1652523653000,"updatedTime":1654527613000,"contributors":[{"name":"00D2","email":"shijinguosjg@gmail.com","commits":3}]},"readingTime":{"minutes":1.41,"words":424},"filePathRelative":"os/windows/Windows10本地硬盘变成了可弹出磁盘.md","localizedDate":"2022年5月14日","excerpt":"<h2> 问题</h2>\\n<p>装好系统之后，硬盘在右下角显示为可安全删除设备。</p>\\n<h2> 原因</h2>\\n<p>Standard SATA AHCI controller使用高性能的硬盘模式就会把硬盘看作是一个可热插拔的存储器，所以自然就会显示可弹出。</p>\\n<h2> 解决方法</h2>\\n<ol>\\n<li>\\n<p>如果你的BIOS支持关闭HotSwap或者HotPlug，那么直接关闭就可以了。\\n或者你直接在BIOS里把磁盘模式改为IDE，不过这样就发挥不好你SSD的高性能了。</p>\\n</li>\\n<li>\\n<p>还有一种解决方法，就是手动让系统把这两个磁盘当作本地磁盘。</p>\\n</li>\\n</ol>"}');export{e as data};
